function update(){for(var t=0;t<snowflakes.length;t++)snowflakes[t].update()}function Snow(){this.x=random(0,width),this.y=random(-height,0),this.radius=random(.5,3),this.speed=random(1,10),this.wind=random(-.5,3)}function createSnow(t){for(var n=0;n<t;n++)snowflakes[n]=new Snow}function draw(){ctx.clearRect(0,0,ctx.canvas.width,ctx.canvas.height);for(var t=0;t<snowflakes.length;t++)snowflakes[t].draw()}function loop(){draw(),update(),animFrame(loop)}function random(t,n){var o=(t+Math.random()*(n-t)).toFixed(1);return o=Math.round(o)}var canvas=document.getElementById("snow"),ctx=canvas.getContext("2d"),width=ctx.canvas.width=document.body.offsetWidth,height=ctx.canvas.height=document.body.offsetHeight,animFrame=window.requestAnimationFrame||window.mozRequestAnimationFrame||window.webkitRequestAnimationFrame||window.msRequestAnimationFrame,snowflakes=[];window.onresize=function(){width=ctx.canvas.width=document.body.offsetWidth,height=ctx.canvas.height=document.body.offsetHeight},Snow.prototype.draw=function(){ctx.beginPath(),ctx.arc(this.x,this.y,this.radius,0,2*Math.PI),ctx.fillStyle="#FEF0ED",ctx.fill(),ctx.closePath()},Snow.prototype.update=function(){this.y+=this.speed,this.x+=this.wind,this.y>ctx.canvas.height&&(this.y=0,this.x=random(0,width))},createSnow(150),loop();